        const { id, name } = person;
        return (
          <div key={id} className="item">
            <h4>{name}</h4>
            <button onClick={() => removeItem(id)}>Remove</button>
          </div>
        );
      })}
      <button className="btn" onClick={() => gitsetPeople([])}>
        Clear Items
      </button>
    </>
  );
};

export default UseStateArray;
